select  o.operating_unit_id unidad_trabajo, 
     (select uo.name
        from open.or_operating_unit uo
        where uo.operating_unit_id = o.operating_unit_id) unidad_trabajo_desc,
       g.order_id, 
       o.order_status_id estado_orden,
       o.causal_id causal,
       (select d.description from open.ge_geogra_location d where d.geograp_location_id = ci.departamento) descripcion_departamento,
       s.sesususc,
       g.product_id,
      ci.edad edadinicial,
      (select z.edad
        from open.ldc_osf_sesucier z
          where  g.product_id = z.producto
              ---ci.producto = z.producto
              and z.nuano = 2024 --:a?ocierrefin
              and z.numes = 6 /*:mescierrefin*/) edad_final,
       decode((select z.edad
        from open.ldc_osf_sesucier z
          where  g.product_id = z.producto
              ---ci.producto = z.producto
              and z.nuano = 2024 --:a?ocierrefin
              and z.numes = 6 /*:mescierrefin*/),-1, 'S',0, 'S',30, 'S',60, 'S',null,'S','N') recuperado,         
      decode( (select z.estado_financiero
        from open.ldc_osf_sesucier z
          where  g.product_id = z.producto
              ---ci.producto = z.producto
              and z.nuano = 2024 --:a?ocierrefin
              and z.numes = 6 /*:mescierrefin*/), 'C','S','N') castigado,
              
       decode(g.debt_age,-1, 'PM',0, 'PM',30, '30Dias',60, '60Dias', 90,'90Dias', 120,'120Dias', '>120Dias') edadini, 
          (ci.sesusape+ci.deuda_no_corriente) deuda_tota,
          decode(ci.tipo_producto, 7014, 'GAS', 'FNB') tipo_ptoducto
from open.gc_coll_mgmt_pro_det g 
inner join  open.or_order o on o.order_id = g.order_id 
inner join  open.ldc_osf_sesucier ci on g.product_id = ci.producto
inner join servsusc s  on s.sesunuse = g.product_id
where ci.nuano = 2024--:a?o_cierre 
and ci.numes = 6 --:mes_cierre 
and trunc(o.created_date) >= '01/07/2024' --:fechaini 
and trunc(o.created_date) <= '31/07/2024' --:fechafin
