SELECT OB.ID_ITEMS_SERIADO CODIGO_ITEM,
       OB.SERIE SERIE_ITEM,
       OB.FECHA_INGRESO FECHA_CREACION,
       I.DESCRIPTION TIPO_INSTRUMENTO,
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO = OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90019482) AND ROWNUM=1) MARCA,
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO = OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90008876) AND ROWNUM=1) RANGO,
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO = OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90008870) AND ROWNUM=1) DIVISION_ESCALA,
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO = OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90008878) AND ROWNUM=1) UNIDAD_MEDIDA,
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO = OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90008872) AND ROWNUM=1) MODELO,
       CASE WHEN OB.PROPIEDAD = 'E' THEN
          TRC.EXEC_FRECUENCY 
       ELSE
          (SELECT TO_NUMBER(EQ.TARGET_VALUE) FROM OPEN.GE_EQUIVALENC_VALUES EQ WHERE EQ.EQUIVALENCE_SET_ID = 12345 AND EQ.ORIGIN_VALUE = I.ITEMS_ID AND ROWNUM=1)
       END FRECUENCIA_CALIBRACION,      
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO=OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90008877) AND ROWNUM=1) CODIGO_INTERNO,
      (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO=OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90019481) AND ROWNUM=1) SOFTWARE,
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO=OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90019483) AND ROWNUM=1) PROVEEDOR,
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO=OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90019482) AND ROWNUM=1) FABRICANTE,
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO=OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90029067) AND ROWNUM=1) FECHA_LEVANTE,
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO=OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90029188) AND ROWNUM=1) FECHA_RECEPCION,
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO=OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90019484) AND ROWNUM=1) FECHA_DE_PUESTA_EN_SERVICIO,
       OB.ESTADO_TECNICO ESTADO_EQUIPO,
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO=OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90028886) AND ROWNUM=1) TIPO_CALIBRACION,
       'VER ARCHIVO ACTIVO MANUALES' MANUALES,
       'VER CERTIFICADO DE CALIBRACIÓN' PATRON_UTILIZADO,
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO=OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90008866) AND ROWNUM=1) EXACTITUD,
       --OU.OPERATING_UNIT_ID COD_UBICACION,
       --OU.NAME UBICACION,
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO=OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90028862) AND ROWNUM=1) UBICACION,
       (SELECT VALOR FROM OPEN.GE_ITEMS_TIPO_AT_VAL ATR WHERE ATR.ID_ITEMS_SERIADO=OB.ID_ITEMS_SERIADO AND ATR.ID_ITEMS_TIPO_ATR = (SELECT A1.ID_ITEMS_TIPO_ATR FROM OPEN.GE_ITEMS_TIPO_ATR A1 WHERE ID_ITEMS_TIPO=I.ID_ITEMS_TIPO AND ENTITY_ATTRIBUTE_ID=90028880) AND ROWNUM=1) NOVEDADES 
FROM   OPEN.GE_ITEMS_SERIADO OB, 
       OPEN.GE_ITEMS I LEFT OUTER JOIN (select * from OPEN.IF_MAINT_ITEMSER TRC1,OPEN.GE_ITEMS CA where CA.ITEMS_ID = TRC1.ACTIVITY AND  UPPER(CA.DESCRIPTION) LIKE '%CALIBRACI%') TRC
       ON TRC.ITEM_ID = I.ITEMS_ID
             LEFT OUTER JOIN OPEN.OR_ORDER_ACTIVITY ACA ON ACA.ACTIVITY_ID = trc.ITEMS_ID,
       OPEN.GE_ITEMS_TIPO T
WHERE OB.ITEMS_ID = I.ITEMS_ID 
  AND I.ID_ITEMS_TIPO = T.ID_ITEMS_TIPO 
  AND T.SERIADO = 'Y'  
  AND OB.ID_ITEMS_SERIADO = DECODE({?CODIGO},-1,OB.ID_ITEMS_SERIADO,{?CODIGO})
  AND OB.SERIE = DECODE({?SERIE},'-1',OB.SERIE,{?SERIE})
GROUP BY OB.ITEMS_ID,
       OB.SERIE,
       OB.FECHA_INGRESO,
       I.ITEMS_ID,
       I.DESCRIPTION,
       I.ID_ITEMS_TIPO,
       OB.ID_ITEMS_SERIADO,
       TRC.EXEC_FRECUENCY,
       OB.ESTADO_TECNICO,
       OB.PROPIEDAD,
       'VER ARCHIVO ACTIVO MANUALES',
       'VER CERTIFICADO DE CALIBRACIÓN'
       --OU.OPERATING_UNIT_ID,
       --OU.NAME
