   IF(ut_object.fboExistObject(DEL_ROOT_37_.tbObjectToDelete(nuIndex)) and not fblIsObjectUseByRule(DEL_ROOT_37_.tbObjectToDelete(nuIndex))) THEN
    blObjectDeleted := ut_object.fboDeleteObject(DEL_ROOT_37_.tbObjectToDelete(nuIndex));
    ut_trace.trace('  * Borrado ' || DEL_ROOT_37_.tbObjectToDelete(nuIndex), 1);
   END IF;
